mapper.update:
UPDATE blbd
   SET inq_cn = inq_cn + 1
 WHERE seq_no = #{seqNo}

UPDATE blbd
   SET mdfp_nm = #{mdfpNm}, 
       bult_typ_cd = #{bultTypCd},
       bult_titl_nm = #{bultTitlNm}, 
       brcn = #{brcn}
 WHERE seq_no = #{seqNo}

UPDATE blbd
   SET del_yn = 'Y', 
       mdfp_nm = #{mdfpNm}
 WHERE seq_no = #{seqNo}

mapper.select:
SELECT COUNT(*) 	AS totalCount
  FROM blbd
 WHERE bult_typ_cd = #{bultTypCd}
