mapper.select:
SELECT T01.seq_no, /* 일련번호 */ T02.user_nm, /* 사용자 이름 */ T02.tel_no, /* 전화번호 */ T02.dty_nm, /* 직급 */ T02.blg_nm, /* 소속 */ T01.bas_dt, /* 기준일자 */ T01.svce_form_cd, /* 근무형태코드 */ T03.dtl_cnm AS svce_form_cd_nm, /* 근무형태코드명 */ T01.st_dt, /* 시작일자 */ T01.st_tm, /* 시작시각 */ T01.ed_dt, /* 종료일자 */ T01.ed_tm, /* 종료시각 */ ( SELECT COUNT(*) FROM hldy_info WHERE (bas_dt BETWEEN T01.st_dt AND T01.ed_dt) AND hldy_yn = 'N' ) AS prd, /* 전체 기간 일수 */ T01.rg_dtm, /* 상신일시 */ T01.dcz_sts_cd, /* 결재상태 */ T04.dtl_cnm AS dcz_sts_cd_nm, /* 결재상태명 */ ( SELECT ( (SELECT asg_dys + ans_dys FROM vctn_asg WHERE bas_yy = DATE_FORMAT(NOW(), '%Y') AND user_id = T01.user_id) - (SELECT IFNULL(SUM(sbt_num), 0) FROM dut_hst WHERE sbt_num != 0 AND user_id = T01.user_id AND dcz_sts_cd = 'CONFIRM') ) FROM DUAL ) AS sbt_aft_vctn_dys, /* 차감 후 휴가 일수 = (올해 사용 가능한 휴가 일수) - (승인받은 휴가일수) */ T01.dcz_dtm, /* 결재일시 */ T01.dczmn_id /* 결재자이름 */ FROM dut_hst T01 /* 근태기록 */ LEFT JOIN mem_bsc_info T02 /* 기본정보 */ ON T01.user_id = T02.user_id LEFT JOIN ( SELECT ds_cd, dtl_ds_cd, dtl_cd, dtl_ds_cd_nm, dtl_cnm FROM comn_cd WHERE ds_cd = 'DUTY' ) T03 ON T01.svce_form_cd = T03.dtl_cd /* 근무 형태 코드 */ LEFT JOIN ( SELECT ds_cd, dtl_cd, dtl_cnm FROM comn_cd WHERE ds_cd = 'CONFIRM' ) T04 ON T01.dcz_sts_cd = T04.dtl_cd /* 결재 상태 코드 */ WHERE 1=1
AND T01.user_id = #{userId}
AND T01.svce_form_cd = #{searchType}
AND T02.user_nm = #{searchKeyword}
AND DATE_FORMAT(T01.bas_dt, '%Y') = #{searchDate}
ORDER BY T01.seq_no DESC

mapper.update:
UPDATE dut_hst
   SET svce_form_cd = #{svceFormCd},
   	   ed_dt = #{edDt}, 
   	   ed_tm = #{edTm}, 
   	   ckof_btn_clk_dtm = #{ckofBtnClkDtm}, 
   	   sbt_num = #{sbtNum}
 WHERE user_id = #{userId}
   AND seq_no = #{seqNo}

mapper.select:
	SELECT COUNT(*) 
	  FROM dut_hst T01
 LEFT JOIN mem_bsc_info T02
 		ON T01.user_id = T02.user_id
	 WHERE 1=1

 		AND DATE_FORMAT(bas_dt, '%Y') = DATE_FORMAT(NOW(), '%Y')


 		AND T01.user_id = #{userId}



 		AND svce_form_cd = #{searchType}



 		AND T02.user_nm = #{searchKeyword}



 		AND DATE_FORMAT(bas_dt, '%Y') = #{searchDate}
